(422, 'dbem_bookings_contact_email_subject', 'New Booking', 'yes'),
(423, 'dbem_bookings_contact_email_body', '#_BOOKINGNAME (#_BOOKINGEMAIL) will attend #_EVENTNAME on #_EVENTDATES. He wants to reserve #_BOOKINGSPACES spaces.\r\n\r\n Now there are #_BOOKEDSPACES spaces reserved, #_AVAILABLESPACES are still available.\r\n\r\nYours faithfully,\r\n\r\nEvents Manager - http://wp-events-plugin.com\r\n\r\n\r\n-------------------------------\r\n\r\nPowered by Events Manager - http://wp-events-plugin.com', 'yes'),
(424, 'dbem_contactperson_email_cancelled_subject', 'Booking Cancelled', 'yes'),
(425, 'dbem_contactperson_email_cancelled_body', '#_BOOKINGNAME (#_BOOKINGEMAIL) cancelled his booking at #_EVENTNAME on #_EVENTDATES. He wanted to reserve #_BOOKINGSPACES spaces.\r\n\r\n Now there are #_BOOKEDSPACES spaces reserved, #_AVAILABLESPACES are still available.\r\n\r\nYours faithfully,\r\n\r\nEvents Manager - http://wp-events-plugin.com\r\n\r\n\r\n-------------------------------\r\n\r\nPowered by Events Manager - http://wp-events-plugin.com', 'yes'),
(426, 'dbem_bookings_email_pending_subject', 'Booking Pending', 'yes'),
(427, 'dbem_bookings_email_pending_body', 'Dear #_BOOKINGNAME, \r\n\r\nYou have requested #_BOOKINGSPACES space/spaces for #_EVENTNAME.\r\n\r\nWhen : #_EVENTDATES @ #_EVENTTIMES\r\n\r\nWhere : #_LOCATIONNAME - #_LOCATIONFULLLINE\r\n\r\nYour booking is currently pending approval by our administrators. Once approved you will receive an automatic confirmation.\r\n\r\nYours faithfully,\r\n\r\n#_CONTACTNAME\r\n\r\n\r\n-------------------------------\r\n\r\nPowered by Events Manager - http://wp-events-plugin.com', 'yes'),
(428, 'dbem_bookings_email_rejected_subject', 'Booking Rejected', 'yes'),
(429, 'dbem_bookings_email_rejected_body', 'Dear #_BOOKINGNAME, \r\n\r\nYour requested booking for #_BOOKINGSPACES spaces at #_EVENTNAME on #_EVENTDATES has been rejected.\r\n\r\nYours faithfully,\r\n\r\n#_CONTACTNAME\r\n\r\n\r\n-------------------------------\r\n\r\nPowered by Events Manager - http://wp-events-plugin.com', 'yes'),
(430, 'dbem_bookings_email_confirmed_subject', 'Booking Confirmed', 'yes'),
(431, 'dbem_bookings_email_confirmed_body', 'Dear #_BOOKINGNAME, \r\n\r\nYou have successfully reserved #_BOOKINGSPACES space/spaces for #_EVENTNAME.\r\n\r\nWhen : #_EVENTDATES @ #_EVENTTIMES\r\n\r\nWhere : #_LOCATIONNAME - #_LOCATIONFULLLINE\r\n\r\nYours faithfully,\r\n\r\n#_CONTACTNAME\r\n\r\n\r\n-------------------------------\r\n\r\nPowered by Events Manager - http://wp-events-plugin.com', 'yes'),
(432, 'dbem_bookings_email_cancelled_subject', 'Booking Cancelled', 'yes'),
(433, 'dbem_bookings_email_cancelled_body', 'Dear #_BOOKINGNAME, \r\n\r\nYour requested booking for #_BOOKINGSPACES spaces at #_EVENTNAME on #_EVENTDATES has been cancelled.\r\n\r\nYours faithfully,\r\n\r\n#_CONTACTNAME\r\n\r\n\r\n-------------------------------\r\n\r\nPowered by Events Manager - http://wp-events-plugin.com', 'yes'),
(434, 'dbem_bookings_email_registration_subject', '[El Pud√∫ Pisador] Your username and password', 'yes'),
(435, 'dbem_bookings_email_registration_body', 'You have successfully created an account at El Pud√∫ Pisador\r\n\r\nYou can log into our site here : http://localhost/wp-login.php\r\n\r\nUsername : %username%\r\n\r\nPassword : %password%\r\n\r\nTo view your bookings, please visit http://localhost/?page_id=11 after logging in.', 'yes'),
